package javaStudy;

public class OperatorExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i1 = -5;
		int i2 = -i1;
		int i3 = -i1;

		System.out.println(i2);
		
		int i4 = ++i3;
		System.out.println(i4);
		
		double i = 5;
		double j = 2;
		
		System.out.println(i / 2);
	}

}
